var num = 0;
document.getElementById("div_btn").onclick = function() {
   var img = document.getElementById('img_title');
   var txt = document.getElementById('text_value');
   var pgb = document.getElementById('div_progress_bar');

   num = (++num)%4;
   switch(num){
      case 0:
         img.src = "images/food0.png";
         txt.textContent = "700";
         pgb.style.width = "70%"
         break;
      case 1:
         img.src = "images/food1.png";
         txt.textContent = "500";
         pgb.style.width = "50%"
         break;
      case 2:
         img.src = "images/food2.jpg";
         txt.textContent = "400";
         pgb.style.width = "40%"
         break;
      case 3:
         img.src = "images/food3.png";
         txt.textContent = "800";
         pgb.style.width = "80%"
         break;
   }




   //var text = document.getElementById('');
};
