function run() {
  print(document.firstChild.nodeName);
  var htmlNode = document.firstChild.nextSibling;
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "xx-small";
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "x-small";
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "small";
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "medium";
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "large";
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "x-large";
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "xx-large";
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "larger";
  print(htmlNode.style.fontSize);
  htmlNode.style.fontSize = "smaller";
  print(htmlNode.style.fontSize);
  print("done");
}
run();
