node.text:
#box1 {
    background-color: maroon;
    color: white;
}
#box2 {
    background-color: navy;
    color: white;
    padding-right: 50px;
}

#box3 {
    background-color: orange;
    padding-right: 60%;
}

        function run() {
            document.body.style.paddingRight = "Initial";
            print("div1 set paddingRight(\"Initial\"): " + document.body.style.paddingRight);

            document.body.style.paddingRight = "inherit";
            print("div1 set paddingRight(\"inherit\"): " + document.body.style.paddingRight);

            document.body.style.paddingRight = "20pX";
            print("div1 set paddingRight(\"20pX\"): " + document.body.style.paddingRight);

            document.body.style.paddingRight = "40%";
            print("div1 set paddingRight(\"40%\"): " + document.body.style.paddingRight);

            document.body.style.paddingRight = "5gt";
            print("div1 set paddingRight(\"5it\"): " + document.body.style.paddingRight + " (egnore invalid unit)");

            document.body.style.paddingRight = "-7px";
            print("div1 set paddingRight(\"-7px\"): " + document.body.style.paddingRight + " (egnore negative value)");
        }
        run();
