node.text:
function run() {
    document.body.style.paddingTop = "Initial";
    print("div1 set paddingTop(\"Initial\"): " + document.body.style.paddingTop);

    document.body.style.paddingTop = "inherit";
    print("div1 set paddingTop(\"inherit\"): " + document.body.style.paddingTop);

    document.body.style.paddingTop = "20pX";
    print("div1 set paddingTop(\"20pX\"): " + document.body.style.paddingTop);

    document.body.style.paddingTop = "40%";
    print("div1 set paddingTop(\"40%\"): " + document.body.style.paddingTop);

    document.body.style.paddingTop = "5gt";
    print("div1 set paddingTop(\"5it\"): " + document.body.style.paddingTop + " (egnore invalid unit)");

    document.body.style.paddingTop = "-7px";
    print("div1 set paddingTop(\"-7px\"): " + document.body.style.paddingTop + " (egnore negative value)");
}
run();
