node.text:
#idText {
    font-size: 20px;                
    color: red;
}
.classText {
    font-size: 12px;
    text-align: 16px;
}
div {
    display: block;
}

        function test() {
			print("<Element.prototype.classList>")
			print(document.firstElementChild.lastElementChild.children.item(1).classList);

			print("<DOMTokenList::length>")
			print(document.firstElementChild.lastElementChild.children.item(1).classList.length);

			print("<DOMTokenList::item>")
			print(document.firstElementChild.lastElementChild.children.item(1).classList.item(0));
			print(document.firstElementChild.lastElementChild.children.item(1).classList.item(1));
			print(document.firstElementChild.lastElementChild.children.item(1).classList.item(2));

			print("<DOMTokenList::contains>")
			print(document.firstElementChild.lastElementChild.children.item(1).classList.contains("classText1"));
			print(document.firstElementChild.lastElementChild.children.item(1).classList.contains("classText0"));

			print("<DOMTokenList::add>")
			document.firstElementChild.lastElementChild.children.item(1).classList.add("classText3");
			document.firstElementChild.lastElementChild.children.item(1).classList.add("classText4", "classText5");
			var len = document.firstElementChild.lastElementChild.children.item(1).classList.length;
			for (var i = 0; i < len; i++)
				print(document.firstElementChild.lastElementChild.children.item(1).classList.item(i));

			print("<DOMTokenList::remove>")
			document.firstElementChild.lastElementChild.children.item(1).classList.remove("classText3");
			document.firstElementChild.lastElementChild.children.item(1).classList.remove("classText1", "classText5");
			var len = document.firstElementChild.lastElementChild.children.item(1).classList.length;
			for (var i = 0; i < len; i++)
				print(document.firstElementChild.lastElementChild.children.item(1).classList.item(i));

			print("<DOMTokenList::toggle, force=true>")
			document.firstElementChild.lastElementChild.children.item(1).classList.toggle("classText1", true);
			document.firstElementChild.lastElementChild.children.item(1).classList.toggle("classText4", true);
			var len = document.firstElementChild.lastElementChild.children.item(1).classList.length;
			for (var i = 0; i < len; i++)
				print(document.firstElementChild.lastElementChild.children.item(1).classList.item(i));

			print("<DOMTokenList::toggle, force=false>")
			document.firstElementChild.lastElementChild.children.item(1).classList.toggle("classText4", false);
			document.firstElementChild.lastElementChild.children.item(1).classList.toggle("classText5", false);
			var len = document.firstElementChild.lastElementChild.children.item(1).classList.length;
			for (var i = 0; i < len; i++)
				print(document.firstElementChild.lastElementChild.children.item(1).classList.item(i));

			print("<DOMTokenList::toggle, no force>")
			document.firstElementChild.lastElementChild.children.item(1).classList.toggle("classText3");
			document.firstElementChild.lastElementChild.children.item(1).classList.toggle("classText2");
			var len = document.firstElementChild.lastElementChild.children.item(1).classList.length;
			for (var i = 0; i < len; i++)
				print(document.firstElementChild.lastElementChild.children.item(1).classList.item(i));

			print("<End>")
            return 1<2;
        }
		test();
