node.text:
function run() {
  print(document.firstChild);
  var htmlNode = document.firstChild.nextSibling;
  print("testing appendChild(), removeChild(), insertBefore(), replaceChild()");
  var bodyNode = htmlNode.lastChild;
  var child = bodyNode.removeChild(bodyNode.firstChild);
  print("DIV == " + bodyNode.firstChild.nodeName);
  bodyNode.appendChild(child);
  print("#text == " + bodyNode.lastChild.nodeName);

  var c = bodyNode.removeChild(bodyNode.lastChild);
  print("#text == " + c.nodeName);
  bodyNode.insertBefore(c, bodyNode.firstChild);
  print("#text == " + bodyNode.firstChild.nodeName);

  var c = bodyNode.removeChild(bodyNode.firstChild);
  bodyNode.replaceChild(c, bodyNode.firstChild);
  print("#text == " + bodyNode.firstChild.nodeName);
  print("done");
}
run();
