        var cities = [];
        var curCity = 0;

        function init() {
            initCities();
            setDefaultEvents();
            showTime(curCity);
        }

        function initCities() {
            cities.push({city: "Seoul", gmt: 9});
            cities.push({city: "San Francisco", gmt: -8});
            cities.push({city: "London", gmt: 0});
        }

